import * as React from 'react';
import { useState } from 'react';
import { Button, TextField, Typography, Grid, Paper, Avatar, CssBaseline, Box, Modal, Form, FormControl, InputLabel, Input, InputAdornment, IconButton } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import { createTheme, ThemeProvider } from '@mui/material/styles';

function SignInSide() {
  const [addNewUser, setAddNewUser] = useState(false);
  const [newUser, setNewUser] = useState({ "handle": "", "userName": "", "email": "", "subscribedBlogs": [""] });

  const handleSignUpClick = () => {
    setAddNewUser(true);
  };

  const handleClose = () => {
    setAddNewUser(false);
  };

  const addSingleUser = () => {
    // Add logic for adding single user
  };

  return (
    <ThemeProvider theme={createTheme()}>
      <CssBaseline />
      <Grid container component="main" sx={{ height: '100vh', justifyContent: 'center', alignItems: 'center' }}>
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign In
            </Typography>
            <Button variant="contained" onClick={handleSignUpClick} sx={{ mt: 3, mb: 2 }}>Don't have an account? Sign Up</Button>
          </Box>
        </Grid>
      </Grid>

      <Modal
        open={addNewUser}
        onClose={handleClose}
        aria-labelledby="signup-modal"
        aria-describedby="signup-form"
      >
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            bgcolor: 'background.paper',
            boxShadow: 24,
            p: 4,
            width: 400,
            borderRadius: 4
          }}
        >
          <Typography component="h1" variant="h5" align="center">
            Sign Up
          </Typography>
          <Form sx={{ mt: 2 }} noValidate>
            <FormControl fullWidth sx={{ my: 1 }}>
              <InputLabel htmlFor="handle">Codeforces Handle</InputLabel>
              <Input
                id="handle"
                type="text"
                value={newUser.handle}
                onChange={(e) => setNewUser({ ...newUser, handle: e.target.value })}
              />
            </FormControl>
            <FormControl fullWidth sx={{ my: 1 }}>
              <InputLabel htmlFor="username">Username</InputLabel>
              <Input
                id="username"
                type="text"
                value={newUser.userName}
                onChange={(e) => setNewUser({ ...newUser, userName: e.target.value })}
              />
            </FormControl>
            <FormControl fullWidth sx={{ my: 1 }}>
              <InputLabel htmlFor="email">Email Address</InputLabel>
              <Input
                id="email"
                type="email"
                value={newUser.email}
                onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
              />
            </FormControl>
            <Button
              fullWidth
              variant="contained"
              onClick={addSingleUser}
              sx={{ mt: 2 }}
            >
              Sign Up
            </Button>
          </Form>
        </Box>
      </Modal>
    </ThemeProvider>
  );
}

export default SignInSide;
