import * as React from 'react';
import { useState } from 'react';
import { Button, TextField, Typography, Grid, Paper, Avatar } from '@mui/material';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Modal, Form } from 'react-bootstrap';

function SignInSide() {
  const [addNewUser, setAddNewUser] = useState(false);
  const [newUser, setNewUser] = useState({ "handle": "", "userName": "", "email": "", "subscribedBlogs": [""] });

  const handleSignUpClick = () => {
    setAddNewUser(true);
  };

  const handleClose = () => {
    setAddNewUser(false);
  };

  const addSingleUser = () => {
    // Add logic for adding single user
  };

  return (
    <ThemeProvider theme={createTheme()}>
      <Grid container component="main" sx={{ height: '100vh', justifyContent: 'center', alignItems: 'center' }}>
        <CssBaseline />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign Up
            </Typography>
            <Button variant="contained" onClick={handleSignUpClick} sx={{ mt: 3, mb: 2 }}>Sign Up</Button>
          </Box>
        </Grid>
      </Grid>

      <Modal show={addNewUser} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Sign Up</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="handle">
              <Form.Label>Codeforces Handle</Form.Label>
              <Form.Control type="text" placeholder="Enter your handle" onChange={(event) => { setNewUser({ ...newUser, handle: event.target.value }) }} />
            </Form.Group>
            <Form.Group controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control type="text" placeholder="Enter your username" onChange={(event) => { setNewUser({ ...newUser, userName: event.target.value }) }} />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email Address</Form.Label>
              <Form.Control type="email" placeholder="Enter your email" onChange={(event) => { setNewUser({ ...newUser, email: event.target.value }) }} />
            </Form.Group>
            <Button variant="contained" onClick={addSingleUser}>Submit</Button>
          </Form>
        </Modal.Body>
      </Modal>
    </ThemeProvider>
  );
}

export default SignInSide;
